public class App_21 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 6;
		int b = 3;
		int c = 8;

		if (a > b && a > c) {
			System.out.println(a);

		} else {
			if (b > c) {
				System.out.println(b);
			} else {
				System.out.println(c);
			}

		}
	}
}
